mapper.update:
update flow_node_attr
a_key = #{key,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, value = #{value,jdbcType=VARCHAR}, flow_id = #{flowId,jdbcType=VARCHAR}, node_id = #{nodeId,jdbcType=VARCHAR}, version = version+1, ts = now(),
where id = #{id,jdbcType=INTEGER}

mapper.select:
select 
from flow_node_attr
where (SELECT flow_container( #{flowId})) like CONCAT('%',flow_id,'%') and node_id=#{nodeId}
