mapper.update:
update flow_node_event
flow_id = #{flowId,jdbcType=VARCHAR}, node_id = #{nodeId,jdbcType=VARCHAR}, e_key = #{key,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, order_index = #{order,jdbcType=INTEGER}, type = #{type,jdbcType=VARCHAR}, event_type = #{eventType,jdbcType=VARCHAR}, content = #{content,jdbcType=VARCHAR}, create_code = #{createCode,jdbcType=VARCHAR}, create_name = #{createName,jdbcType=VARCHAR}, version = version+1, ts = now(),
where id = #{id,jdbcType=INTEGER}

mapper.select:
select 
from flow_node_event
where (SELECT flow_container( #{flowId})) like CONCAT('%',flow_id,'%') and node_id=#{id}
